    @Before
    public void before()
    {
        clientMock = mock(ConsulClient.class);
        serviceMock = mock(NewService.class);
        when(serviceMock.getId()).thenReturn("42");
        service = new ConsulService(clientMock, serviceMock);
    }

    @Test
    public void register_should_invoke_client()
    {
