TIGValue *TIGStringAddEscapeCharacters(TIGValue *tigString)
{
	if (tigString == NULL || strcmp(tigString->type, "String") != 0)
	{
#ifdef TIG_DEBUG
		if (tigString == NULL)
		{
			printf("ERROR Function:TIGStringAddEscapeCharacters() Variable:tigString Equals:NULL\n");
		}
		else if (strcmp(tigString->type, "String") != 0)
		{
			printf("ERROR Function:TIGStringAddEscapeCharacters() Variable:tigString->type Equals:%s Valid:\"String\"\n", tigString->type);
		}

	#ifdef TIG_DEBUG_ASSERT
		assert(0);
	#endif
#endif
		return NULL;
	}

	char *string = tigString->string;

	int extraCount = 0;
	int i;

	for (i = 0; i < strlen(string); i++)
	{
		switch (string[i])
		{
			case '"':
			case '\\':
			case '/':
			case '\b':
			case '\f':
			case '\n':
			case '\r':			
			case '\t':
				extraCount++;
			break;
		}
	}

	if (extraCount > 0)
	{
		char *newString = (char *)malloc((strlen(string) + extraCount + 1) * sizeof(char));
		int index = 0;

		if (newString == NULL)
		{
#ifdef TIG_DEBUG
			printf("ERROR Function:TIGStringAddEscapeCharacters() Variable:newString Equals:NULL\n");

	#ifdef TIG_DEBUG_ASSERT
		assert(0);
	#endif
#endif
			return NULL;
		}

		for (i = 0; i < strlen(string); i++)
		{
			switch (string[i])
			{
				case '\"':
					newString[index] = '\\';
					newString[index + 1] = '"';
					index += 2;
				break;

				case '\\':
					newString[index] = '\\';
					newString[index + 1] = '\\';
					index += 2;
				break;

				case '/':
					newString[index] = '\\';
					newString[index + 1] = '/';
					index += 2;
				break;

				case '\b':
					newString[index] = '\\';
					newString[index + 1] = 'b';
					index += 2;
				break;

				case '\f':
					newString[index] = '\\';
					newString[index + 1] = 'f';
					index += 2;
				break;

				case '\n':
					newString[index] = '\\';
					newString[index + 1] = 'n';
					index += 2;
				break;

				case '\r':
					newString[index] = '\\';
					newString[index + 1] = 'r';
					index += 2;
				break;

				case '\t':
					newString[index] = '\\';
					newString[index + 1] = 't';
					index += 2;
				break;

				default:
					newString[index] = string[i];
					index++;
				break;
			}
		}

		TIGValue *theNewTIGString = TIGStringInput(NULL, newString);

		free(newString);
		newString = NULL;

		return theNewTIGString;
	}

	return tigString;
}

