                mLeactureName = bundle.getString(ARGS_DIALOG_LECTURER_NAME, "");
                mAssistantAvatar = bundle.getString(ARGS_DIALOG_ASSIST_AVATAR, "");
                mAssistantName = bundle.getString(ARGS_DIALOG_ASSIST_NAME, "");
            }
