        if (comment != null) {
            mRbCommentDialogScore.setRating(comment.getScore());
            mEtCommentDialogInput.setText(comment.getContent());
        } else {
            mRbCommentDialogScore.setRating(0);
            mEtCommentDialogInput.setText("");
        }
    }

