template <typename T>
static const char *
type_name()
{
#ifdef __GNUC__
    const int buf_size = 32;
    static char tname[buf_size];
    if (tname[0] != 0) {
        return tname;
    }

    const std::type_info& id = typeid(T);
    int status;
    char *name = abi::__cxa_demangle(id.name(), NULL, 0, &status);
    if (name != NULL) {
        if (status == 0) {
            strncpy(tname, name, buf_size - 1);
