#include <stdio.h>
#include <stdlib.h>

typedef int ElemType;

void swap(ElemType *left,ElemType *right){
    ElemType temp = *left;
    *left = *right;
    *right = temp;
}

void BubbleSort(ElemType *array,int num){
    if(num == 1) return;

    ElemType *left = array,*right = array + 1;
    int key = num;
    while(--key){
        if(*left > *right)
            swap(left,right);
        left++;
        right++;
    }
    BubbleSort(array,--num);
}

